import React from 'react';
import { Avatar, Button, Paper, Grid, Typography, Container, TextField} from "@material-ui/core";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined"
import Input from './Input';

import useStyles from "./styles";

function Auth() {
    const classes = useStyles();

    const isSignup = false;

    const handleSubmit = () => {

    };

    const handleChange = () => {

    };

  return (
    <Container component="main" maxWidth="xs">
        <Paper className={classes.paper} elevation={3}>
            <Avatar className={classes.avatar}>
                <LockOutlinedIcon />
            </Avatar>
            <Typography variant="h5">{isSignup ? 'Registrarse' : 'Iniciar Sesion'}</Typography>
            <form className={classes.form} onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    {
                        isSignup && (
                            <>
                                <Input name="firstName" label="Nombre" handleChange={handleChange} autoFocus xs={6} />
                                <Input name="lastName" label="Apellido" handleChange={handleChange} autoFocus xs={6} />
                            </>
                        )
                    }
                </Grid>
            </form>
        </Paper>
    </Container>
  )
}

export default Auth
