    const classes = useStyles();
    const [comments, setComments] = useState([1, 2, 3, 4]);
    const [comment, setComment] = useState([]);

    const handleClick = () => {

    };

    return (
    <div>
        <div className={classes.commentsOuterContainer}>
            <div className={classes.commentsInnerContainer}>
                <Typography gutterBottom variant="h6">Comentarios</Typography>
                {comments.map((c, i) => (
                    <Typography key={i} gutterBottom variant="subtitle1">
                        Comment {i}
                    </Typography>
                ))}
            </div>
            <div style={{ width: "70px"}}>
                <Typography gutterBottom variant="h6">Escribe un Comentario</Typography>
                <TextField 
                    fullWidth
                    rows={4}
                    variant="outlined"
                    label="Comentar"
                    multiline
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                />
                <Button style={{ marginTop: "10px" }} fullWidth disabled={!comment} variant="contained" onClick={handleClick}>
                    Comentar
                </Button>
            </div>
        </div>
    </div>
  )
}

export default CommentSection
